  const clickHandler = () => {
    setTitle('hellooooo');
  }

  return (
    <Card className="expense-item">
      <ExpenseDate date={props.date} />

      <div className="expense-item__description">
        <h2>{title}</h2>
        <div className="expense-item__price">Â£{props.amount}</div>
      </div>
      <button
        onClick={clickHandler}
      >
        Change Title
      </button>
    </Card>
  );
};

export default ExpenseItem;
